$(document).ready(function () {

    $('#btnGiris').click(function () {

        var ePosta = $('#txtEposta').val();
        var parola = $('#txtParola').val();
        if (ePosta == '') {
            HataMesaji(mesajlar.epostaadresigirilmedi);
            $('#txtEposta').focus();
            return false;
        }
        if (parola == '') {
            HataMesaji(mesajlar.parolagirilmedi);
            $('#txtParola').focus();
            return false;
        }
        
        $( "#frmGiris" ).submit();

    });
});