$(function () {

    $('.btnOdevGonderisiIncelendi').click(function () {

        var dugme = $(this);
        var OdevGonderiID = $(this).attr('data-OdevGonderiID');

        $.ajax({
            type: 'POST',
            url: '/Ajax/Odev/OdevGonderiBilgisiKaydet',
            data: "OdevGonderiID=" + OdevGonderiID + "&Durum=2",
            success: function (msg) {
                var tr = $(dugme).closest('tr');
                tr.remove();

            },
            error: function (msg) {

                HataMesaji(mesajlar.islemsirasindahata);
            }
        });


    });

    $('.checkBoxOdevGonderimiAcKapa').click(function () {

        var dugme = $(this);
        var OdevID = $(this).attr('data-OdevID');
        var alan = 'OdevGonderimi';
        var gonderimAcikKapali = 0;

        if ($(this).prop("checked") == true) {
            gonderimAcikKapali = 1;
        }
        else if ($(this).prop("checked") == false) {
            gonderimAcikKapali = 2;
        }



        $.ajax({
            type: 'POST',
            url: '/Ajax/Odev/OdevAyarKaydet',
            data: "pk=" + OdevID + "&name=" + alan + "&value=" + gonderimAcikKapali,
            success: function (msg) {


            },
            error: function (msg) {

                HataMesaji(mesajlar.islemsirasindahata);
            }
        });


    });

    $('body').on('click', '.btnOdevSil', function () {
        var OdevID = $(this).attr('data-OdevID');

        var satir = $(this).closest('tr');
        if (confirm(mesajlar.odevsilinecek)) {

            $.ajax({
                type: 'POST',
                url: '/Ajax/Odev/OdevSil',
                data: "OdevID=" + OdevID,
                success: function (msg) {

                    satir.remove();
                },
                error: function (msg) {
                    HataMesaji(mesajlar.islemsirasindabirhataolustu);
                }
            });
        }
    });

});