    $('#btnTumuOkundu').click(function () {

        $.ajax({
            type: 'POST',
            url: '/Ajax/Bildirim/TumunuOkunduOlarakIsaretle',
            success: function (msg) {
                $("#lstBildirimler").empty();

            },
            error: function (msg) {
                HataMesaji(mesajlar.islemsirasindahata);
            }
        });
    });

    $('.bildirimOgesiniOkunduIsaretle').click(function () {
        var bildirimID = $(this).attr('data-BildirimID');
        var dugme = $(this);
        $.ajax({
            type: 'POST',
            url: '/Ajax/Bildirim/OkunduOlarakIsaretle',
            data: 'BildirimID=' + bildirimID,
            success: function (msg) {
                var satir = dugme.closest('tr');
                $(satir).animate({ backgroundColor: 'gray' }, 150).fadeOut(150, function () {
                    $(satir).remove()
                });

            },
            error: function (msg) {
                HataMesaji(mesajlar.islemsirasindahata);
            }
        });
    });
