$(document).ready(function () {
    var tabloBildirimler = $('#lstBildirimler').DataTable({
        "pagingType": "simple",
        "language": {
            "url": ayarlar.datatablelangurl
        },
        "bPaginate": true,
        "bLengthChange": false,
        "bFilter": false,
        "bInfo": false,
        "bSort": false
    });



    $('#btnTumuOkundu').click(function () {

        $.ajax({
            type: 'POST',
            url: '/Ajax/Bildirim/TumunuOkunduOlarakIsaretle',
            success: function (msg) {

                tabloBildirimler.clear().draw();
                
            },
            error: function (msg) {
                HataMesaji(mesajlar.islemsirasindahata);
            }
        });
    });

    $('.bildirimOgesiniOkunduIsaretle').click(function () {
        var bildirimID = $(this).attr('data-BildirimID');
        var dugme = $(this);
        $.ajax({
            type: 'POST',
            url: '/Ajax/Bildirim/OkunduOlarakIsaretle',
            data: 'BildirimID=' + bildirimID,
            success: function (msg) {
                var row = tabloBildirimler.row(dugme.parents('tr'));
                row.remove().draw();
                
            },
            error: function (msg) {
                HataMesaji(mesajlar.islemsirasindahata);
            }
        });
    });

});