$(document).ready(function () {

    $('#btnHesapOlustur').click(function () {

        var ePosta = $('#txtEposta').val();
        
        if (ePosta == '') {
            HataMesaji(mesajlar.epostaadresigirilmedi);
            $('#txtEposta').focus();
            return false;
        }
        
        $.ajax({
            type: 'POST',
            url: '/Ajax/Hesap/ParolamiUnuttum',
            data: "EPosta=" + encodeURIComponent(ePosta),
            success: function (msg) {
                 
                if (msg == 'OK') {
                    MesajKutusu(mesajlar.islemtamam,mesajlar.geciciparolaepostayagonderildi)
                }
                else if (msg=='epostakayitlidegil')
                {
                    HataMesaji(mesajlar.epostakayitlidegildi);
                }
            },
            error: function (msg) {
                 
                    HataMesaji(mesajlar.islemsirasindabirhata);
            }
        });
       
    });



});