$(document).ready(function () {
   
    $('#btnHesapOlustur').click(function () {

        var ePosta = $('#txtEposta').val();
        var ad = $('#txtAd').val();
        var soyad = $('#txtSoyad').val();
        var parola = $('#txtParola').val();
        var reParola = $('#txtReParola').val();
        var kayitKodu=$('#txtKayitKodu').val();
        if (ePosta == '') {
            HataMesaji(mesajlar.epostaadresigirilmedi);
            $('#txtEposta').focus();
            return false;
        }

        if (ad == '') {
            HataMesaji(mesajlar.adgirilmedi);
            $('#txtAd').focus();
            return false;
        }

        if (soyad == '') {
            HataMesaji(mesajlar.soyadgirilmedi);
            $('#txtSoyad').focus();
            return false;
        }

        if (parola == '') {
            HataMesaji(mesajlar.parolagirilmedi);
            $('#txtParola').focus();
            return false;
        }

        if (reParola == '') {
            HataMesaji(mesajlar.parolatekrargir);
            $('#txtReParola').focus();
            return false;
        }

        if (kayitKodu == '') {
            HataMesaji(mesajlar.egitmenkayitkodugir);
            $('#txtKayitKodu').focus();
            return false;
        }

        if (parola != reParola) {
            HataMesaji(mesajlar.girilenparolalaruyusmadi);
            $('#txtReParola').focus();
            return false;
        }

        $("#frmYeniKayit").submit();
    });



});